import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Meditation from './components/Meditation'
import Sidebar from './Sidebar'
import MeditationData from './static/MeditationData';



function App() {

  const [mainPost, setmainPost] = useState(MeditationData);
  const [mode, setMode] = useState('meditation');
  let _article = null;
  if(mode === 'meditation'){
    _article = <Meditation/>;
  }else if(mode === 'nothing'){
    _article = 'nothing';
  }

  return (
    <>
      <BrowserRouter>
        <Sidebar />
        <Switch>
          <Route exact path="/" />
          <Route path="/meditation" />
          <Route path="/workout" />
        </Switch>
      </BrowserRouter>
      <section>
      {_article}
      </section>
    </>
  );
}

export default App;
